/* com.innobiz.orange.web.wc.dao.WcApntrSchdlDDao.selectWcApntrSchdlD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.COMP_ID,N.USER_UID,N.APNTR_UID DESC) AS RNUM, N.* 
	FROM (
		SELECT
	
	WA_T.COMP_ID,WA_T.USER_UID,WA_T.APNTR_UID,WA_T.ORDR

FROM WC_APNTR_SCHDL_D WA_T


	ORDER BY ${orderBy}
	ORDER BY WA_T.COMP_ID,WA_T.USER_UID,WA_T.APNTR_UID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wc.dao.WcApntrSchdlDDao.countWcApntrSchdlD */
