mapper.select:
/* com.innobiz.orange.web.wc.dao.WcCatClsBDao.selectWcCatClsB */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* 
	FROM (
		SELECT
	
	T.CAT_ID ,T.COMP_ID , T.SORT_ORDR , T.RESC_ID , T.BGCOL_CD , T.FONT_COLR_CD , T.USE_YN , CONVERT(VARCHAR(19), T.REG_DT, 120) REG_DT, 
    (SELECT RESC_VA FROM WC_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko') ) AS CAT_NM ,

FROM WC_CAT_CLS_B T 


	ORDER BY ${orderBy}
	ORDER BY T.SORT_ORDR ASC 


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wc.dao.WcCatClsBDao.countWcCatClsB */
SELECT COUNT(*) CNT
FROM WC_CAT_CLS_B T

mapper.insert:
/* com.innobiz.orange.web.wc.dao.WcCatClsBDao.insertWcCatClsB */ INSERT INTO WC_CAT_CLS_B
CAT_ID,COMP_ID,SORT_ORDR,RESC_ID,BGCOL_CD,FONT_COLR_CD,USE_YN,CAT_NM,REGR_UID,REG_DT,
#{catId, jdbcType=VARCHAR},#{compId, jdbcType=VARCHAR},#{sortOrdr, jdbcType=VARCHAR},#{rescId, jdbcType=VARCHAR},#{bgcolCd, jdbcType=VARCHAR}, #{fontColrCd, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR},#{catNm, jdbcType=VARCHAR},#{regrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL,

mapper.update:
/* com.innobiz.orange.web.wc.dao.WcCatClsBDao.updateWcCatClsB */ UPDATE WC_CAT_CLS_B
SORT_ORDR = #{sortOrdr, jdbcType=VARCHAR}, BGCOL_CD = #{bgcolCd, jdbcType=VARCHAR}, FONT_COLR_CD = #{fontColrCd, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, CAT_NM = #{catNm, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND CAT_ID = #{catId, jdbcType=VARCHAR} AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wc.dao.WcCatClsBDao.deleteWcCatClsB */
DELETE FROM WC_CAT_CLS_B

	 AND CAT_ID = #{catId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND USE_YN = #{useYn, jdbcType=VARCHAR}
