mapper.select:
/* com.innobiz.orange.web.wc.dao.WcEnvSetupDDao.selectWcEnvSetupD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.COMP_ID,N.USER_UID,N.PAGE_PLT_TYP_CD DESC) AS RNUM, N.* 
	FROM (
		SELECT
	
	WES_T.COMP_ID, WES_T.USER_UID, WES_T.PAGE_PLT_TYP_CD, WES_T.SCHDL_TYP_CD, WES_T.SCHDL_KND_CD, WES_T.SOLA_LUNA_YN,

FROM WC_ENV_SETUP_D WES_T


	ORDER BY ${orderBy}
	ORDER BY WES_T.COMP_ID,WES_T.USER_UID,WES_T.PAGE_PLT_TYP_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wc.dao.WcEnvSetupDDao.countWcEnvSetupD */
SELECT COUNT(*) CNT
FROM WC_ENV_SETUP_D WES_T

