#{compId, jdbcType=CHAR},#{userUid, jdbcType=VARCHAR},#{pagePltTypCd, jdbcType=VARCHAR}, #{schdlTypCd, jdbcType=VARCHAR},#{schdlKndCd, jdbcType=VARCHAR},#{solaLunaYn, jdbcType=CHAR},

