UPDATE WC_ENV_SETUP_D

    

	 SCHDL_TYP_CD = #{schdlTypCd, jdbcType=VARCHAR},
	 SCHDL_KND_CD = #{schdlKndCd, jdbcType=VARCHAR},
	 SOLA_LUNA_YN = #{solaLunaYn, jdbcType=CHAR},
	


     AND COMP_ID = #{compId, jdbcType=CHAR}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 AND PAGE_PLT_TYP_CD = #{pagePltTypCd, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wc.dao.WcEnvSetupDDao.deleteWcEnvSetupD */
