mapper.select:
/* com.innobiz.orange.web.wc.dao.WcNatBDao.selectWcNatB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.SORT_ORDRT.USER_UID ASC) AS RNUM,
T.COMP_ID, T.USER_UID, T.CD, T.RESC_ID, (SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM, T.SORT_ORDR, T.DFT_YN,
FROM WC_NAT_B WC_USER_NAT_BT, PT_RESC_B R
ORDER BY ${orderBy} ORDER BY T.SORT_ORDRT.USER_UID ASC
) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wc.dao.WcNatBDao.countWcNatB */
SELECT COUNT(*) CNT
FROM WC_NAT_B WC_USER_NAT_BT, PT_RESC_B R

mapper.update:
/* com.innobiz.orange.web.pt.dao.WcNatBDao.updateWcNatB */
UPDATE WC_NAT_B WC_USER_NAT_B

	
		 SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},
		 DFT_YN = #{dftYn, jdbcType=CHAR}
	
	
		 CD = #{cd, jdbcType=VARCHAR},
		 RESC_ID = #{rescId, jdbcType=VARCHAR},
	


	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	
		 AND CD = #{cd, jdbcType=VARCHAR}
	
	
		 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wc.dao.WcNatBDao.deleteWcNatB */
DELETE FROM WC_NAT_B WC_USER_NAT_B

	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	
		 AND CD = #{cd, jdbcType=VARCHAR}
	
	
		 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	
	 ${whereSqllet}
