mapper.select:
/* com.innobiz.orange.web.wc.dao.WcPromGuestDDao.selectWcPromGuestD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.SCHDL_ID,N.GUEST_UID DESC) AS RNUM, N.* FROM ( SELECT
WPG_T.SCHDL_ID, WPG_T.GUEST_UID, CASE WHEN GUEST_EMPL_YN = 'Y' THEN (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = WPG_T.GUEST_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) ELSE GUEST_NM END GUEST_NM, WPG_T.GUEST_POSIT_NM, WPG_T.GUEST_DEPT_NM, CASE WHEN GUEST_EMPL_YN = 'Y' THEN (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT ORG_RESC_ID FROM OR_USER_B WHERE USER_UID = WPG_T.GUEST_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) ELSE GUEST_COMP_NM END GUEST_COMP_NM, WPG_T.STAT_CD, WPG_T.OPIN_CONT, WPG_T.GUEST_EMPL_YN, WPG_T.EMAIL, WPG_T.EMAIL_SEND_YN,
FROM WC_PROM_GUEST_D WPG_T
ORDER BY ${orderBy} ORDER BY WPG_T.SCHDL_ID,WPG_T.GUEST_UID
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wc.dao.WcPromGuestDDao.countWcPromGuestD */
SELECT COUNT(*) CNT
FROM WC_PROM_GUEST_D WPG_T

mapper.insert:
/* com.innobiz.orange.web.wc.dao.WcPromGuestDDao.insertWcPromGuestD */ INSERT INTO WC_PROM_GUEST_D
SCHDL_ID, GUEST_UID, GUEST_NM, GUEST_POSIT_NM, GUEST_DEPT_NM, GUEST_COMP_NM, STAT_CD, OPIN_CONT, GUEST_EMPL_YN, EMAIL, EMAIL_SEND_YN,
#{schdlId, jdbcType=VARCHAR}, #{guestUid, jdbcType=VARCHAR}, #{guestNm, jdbcType=NVARCHAR}, #{guestPositNm, jdbcType=NVARCHAR}, #{guestDeptNm, jdbcType=NVARCHAR}, #{guestCompNm, jdbcType=NVARCHAR}, #{statCd, jdbcType=VARCHAR}, #{opinCont, jdbcType=NVARCHAR}, #{guestEmplYn, jdbcType=VARCHAR}, #{email, jdbcType=VARCHAR}, #{emailSendYn, jdbcType=CHAR},

mapper.update:
