AGNT_ID = #{guestNm, jdbcType=NVARCHAR}, AGNT_ID = #{guestPositNm, jdbcType=NVARCHAR}, AGNT_ID = #{guestDeptNm, jdbcType=NVARCHAR}, AGNT_ID = #{guestCompNm, jdbcType=NVARCHAR}, AGNT_ID = #{statCd, jdbcType=VARCHAR}, AGNT_ID = #{opinCont, jdbcType=NVARCHAR}, AGNT_ID = #{guestEmplYn, jdbcType=VARCHAR}, AGNT_ID = #{email, jdbcType=VARCHAR}, AGNT_ID = #{emailSendYn, jdbcType=CHAR},
SCHDL_ID = #{schdlId, jdbcType=VARCHAR}
