mapper.select:
/* com.innobiz.orange.web.wd.dao.WdAnbModLDao.selectWdAnbModL */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.YEAR DESC, N.ANB_TYP_CD DESC, N.ODUR_UID DESC, N.SEQ DESC) AS RNUM, N.* FROM (SELECT
	
	T.YEAR,
	T.ANB_TYP_CD,
	T.ODUR_UID,
	T.SEQ,
	T.MOD_TYP_CD,
	T.MOD_CNT,
	CONVERT(VARCHAR(10), T.USE_YMD, 120) USE_YMD,
	T.MONTH,
	T.APV_NO,
	T.RSON,
	T.MOD_UID,
	CONVERT(VARCHAR(19), T.MOD_DT, 120) MOD_DT,
	T.NOTE,

FROM WD_ANB_MOD_L T

	AND T.YEAR = #{year, jdbcType=INTEGER}
	AND T.ANB_TYP_CD = #{anbTypCd, jdbcType=VARCHAR}
	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.MOD_TYP_CD = #{modTypCd, jdbcType=VARCHAR}
	AND T.MONTH = #{month, jdbcType=INTEGER}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.MOD_TYP_CD IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.YEAR, T.ANB_TYP_CD, T.ODUR_UID, T.SEQ


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wd.dao.WdAnbModLDao.countWdAnbModL */
SELECT COUNT(*) CNT
FROM WD_ANB_MOD_L T

	AND T.YEAR = #{year, jdbcType=INTEGER}
	AND T.ANB_TYP_CD = #{anbTypCd, jdbcType=VARCHAR}
	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.MOD_TYP_CD = #{modTypCd, jdbcType=VARCHAR}
	AND T.MONTH = #{month, jdbcType=INTEGER}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.MOD_TYP_CD IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wd.dao.WdAnbModLDao.insertWdAnbModL */ INSERT INTO WD_ANB_MOD_L
YEAR, ANB_TYP_CD, ODUR_UID, SEQ, MOD_TYP_CD, MOD_CNT, USE_YMD, MONTH, APV_NO, RSON, MOD_UID, MOD_DT, NOTE,
#{year, jdbcType=INTEGER}, #{anbTypCd, jdbcType=VARCHAR}, #{odurUid, jdbcType=VARCHAR}, #{seq, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SEQ),0)+1 FROM WD_ANB_MOD_L WHERE YEAR = #{year, jdbcType=VARCHAR} AND ANB_TYP_CD = #{anbTypCd, jdbcType=VARCHAR} AND ODUR_UID = #{odurUid, jdbcType=VARCHAR}), #{modTypCd, jdbcType=VARCHAR}, NULL#{modCnt, jdbcType=NUMERIC}, GETDATE(),NULL, CONVERT(DATETIME, #{useYmd, jdbcType=VARCHAR}, 120), NULL#{month, jdbcType=INTEGER}, NULL#{apvNo, jdbcType=BIGINT}, #{rson, jdbcType=VARCHAR}, #{modUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120), #{note, jdbcType=VARCHAR},

mapper.delete:
/* com.innobiz.orange.web.wd.dao.WdAnbModLDao.deleteWdAnbModL */
DELETE FROM WD_ANB_MOD_L

	AND YEAR = #{year, jdbcType=INTEGER}
