mapper.select:
/* com.innobiz.orange.web.wd.dao.WdAnbUseLDao.selectWdAnbUseL */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.YEAR DESC, N.ANB_TYP_CD DESC, N.ODUR_UID DESC, N.USE_YMD DESC) AS RNUM, N.* FROM (SELECT
	
	T.YEAR,
	T.ANB_TYP_CD,
	T.ODUR_UID,
	CONVERT(VARCHAR(10), T.USE_YMD, 120) USE_YMD,
	T.CMPL_YN,
	T.USE_CNT,
	CONVERT(VARCHAR(19), T.REG_DT, 120) REG_DT,
	T.APV_NO,
	T.RSON,

FROM WD_ANB_USE_L T

	AND T.YEAR = #{year, jdbcType=INTEGER}
	AND T.ANB_TYP_CD = #{anbTypCd, jdbcType=VARCHAR}
	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.USE_YMD = CONVERT(DATETIME, #{useYmd, jdbcType=VARCHAR}, 120)
	AND T.CMPL_YN = #{cmplYn, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.YEAR, T.ANB_TYP_CD, T.ODUR_UID, T.USE_YMD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wd.dao.WdAnbUseLDao.countWdAnbUseL */
SELECT COUNT(*) CNT
FROM WD_ANB_USE_L T

	AND T.YEAR = #{year, jdbcType=INTEGER}
	AND T.ANB_TYP_CD = #{anbTypCd, jdbcType=VARCHAR}
	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.USE_YMD = CONVERT(DATETIME, #{useYmd, jdbcType=VARCHAR}, 120)
	AND T.CMPL_YN = #{cmplYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wd.dao.WdAnbUseLDao.insertWdAnbUseL */ INSERT INTO WD_ANB_USE_L
YEAR, ANB_TYP_CD, ODUR_UID, USE_YMD, CMPL_YN, USE_CNT, REG_DT, APV_NO, RSON,
