mapper.select:
/* com.innobiz.orange.web.wh.dao.WhCatGrpBDao.selectWhCatGrpB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* FROM (SELECT
T.CAT_GRP_ID, T.COMP_ID, (SELECT RESC_VA FROM WH_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS CAT_GRP_NM, T.RESC_ID, T.USE_YN, T.DFT_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM WH_CAT_GRP_B T
ORDER BY ${orderBy} ORDER BY T.REG_DT
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wh.dao.WhCatGrpBDao.countWhCatGrpB */
SELECT COUNT(*) CNT
FROM WH_CAT_GRP_B T

mapper.insert:
/* com.innobiz.orange.web.wh.dao.WhCatGrpBDao.insertWhCatGrpB */ INSERT INTO WH_CAT_GRP_B
CAT_GRP_ID, COMP_ID, CAT_GRP_NM, RESC_ID, USE_YN, DFT_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{catGrpId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{catGrpNm, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{dftYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.wh.dao.WhCatGrpBDao.updateWhCatGrpB */ UPDATE WH_CAT_GRP_B
CAT_GRP_NM = #{catGrpNm, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, DFT_YN = #{dftYn, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND CAT_GRP_ID = #{catGrpId, jdbcType=VARCHAR} AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wh.dao.WhCatGrpBDao.deleteWhCatGrpB */
DELETE FROM WH_CAT_GRP_B

	 AND CAT_GRP_ID = #{catGrpId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
