mapper.select:
/* com.innobiz.orange.web.wh.dao.WhMdBDao.selectWhMdB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* FROM (SELECT
T.MD_ID, T.COMP_ID, (SELECT RESC_VA FROM WH_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS MD_NM, T.RESC_ID, T.MD_PID, T.MD_TYP_CD, T.CAT_GRP_ID, T.USE_YN, T.SORT_ORDR, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM WH_MD_B T
ORDER BY ${orderBy} ORDER BY T.REG_DT
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wh.dao.WhMdBDao.countWhMdB */
SELECT COUNT(*) CNT
FROM WH_MD_B T

mapper.insert:
/* com.innobiz.orange.web.wh.dao.WhMdBDao.insertWhMdB */ INSERT INTO WH_MD_B
MD_ID, COMP_ID, MD_NM, RESC_ID, MD_PID, MD_TYP_CD, CAT_GRP_ID, USE_YN, SORT_ORDR, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{mdId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{mdNm, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{mdPid, jdbcType=VARCHAR}, #{mdTypCd, jdbcType=CHAR}, #{catGrpId, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.delete:
/* com.innobiz.orange.web.wh.dao.WhMdBDao.deleteWhMdB */
DELETE FROM WH_MD_B

	 AND MD_ID = #{mdId, jdbcType=VARCHAR}
	 AND MD_ID IN ( #{item, jdbcType=VARCHAR} )

mapper.select:
/* com.innobiz.orange.web.wh.dao.WhMdBDao.maxWhMdB */
SELECT MAX(ISNULL(SORT_ORDR,0))+1 AS CNT
FROM WH_MD_B T

 AND COMP_ID = #{compId, jdbcType=VARCHAR}
 AND T.COMP_ID IN #{item, jdbcType=VARCHAR}
 AND MD_PID = #{mdPid, jdbcType=VARCHAR}
