mapper.select:
/* com.innobiz.orange.web.wh.dao.WhReqOngdHDao.selectWhReqOngdH */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* FROM (SELECT
T.HST_NO, T.REQ_NO, T.PICH_UID, T.CMPL_DUE_DT, T.ONGO_CONT, T.REGR_UID, T.REG_DT, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.PICH_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) PICH_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM,
FROM WH_REQ_ONGD_H T
ORDER BY ${orderBy} ORDER BY T.REQ_NO
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wh.dao.WhReqOngdHDao.countWhReqOngdH */
SELECT COUNT(*) CNT
FROM WH_REQ_ONGD_H T

mapper.insert:
/* com.innobiz.orange.web.wh.dao.WhReqOngdHDao.insertWhReqOngdH */ INSERT INTO WH_REQ_ONGD_H
HST_NO, REQ_NO, PICH_UID, CMPL_DUE_DT, ONGO_CONT, REGR_UID, REG_DT,
#{hstNo, jdbcType=INTEGER}, #{reqNo, jdbcType=INTEGER}, #{pichUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{cmplDueDt, jdbcType=VARCHAR}, 120), #{ongoCont, jdbcType=NVARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.wh.dao.WhReqOngdHDao.updateWhReqOngdH */
UPDATE WH_REQ_ONGD_H

	 PICH_UID = #{pichUid, jdbcType=VARCHAR},
	 
		 CMPL_DUE_DT = GETDATE(),
		 CMPL_DUE_DT = NULL,
		 CMPL_DUE_DT = CONVERT(DATETIME, #{cmplDueDt, jdbcType=VARCHAR}, 120),
		
	 ONGO_CONT = #{ongoCont, jdbcType=NVARCHAR},


	 AND REQ_NO = #{reqNo, jdbcType=VARCHAR}
	 AND HST_NO = #{hstNo, jdbcType=INTEGER}

mapper.delete:
/* com.innobiz.orange.web.wh.dao.WhReqOngdHDao.deleteWhReqOngdH */
DELETE FROM WH_REQ_ONGD_H

	 AND REQ_NO = #{reqNo, jdbcType=VARCHAR}
	 AND HST_NO = #{hstNo, jdbcType=INTEGER}
