mapper.select:
	/* com.innobiz.orange.web.wh.dao.WhReqRelDDao.selectWhReqRelD */
	
	SELECT * FROM (
	
	SELECT
	
		
		TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REQ_NO ASC) AS RNUM, N.* FROM (SELECT
		
		T.REQ_NO,
        T.REL_NO,
        T.SORT_ORDR,
	
	FROM WH_REQ_REL_D T
	
	
		ORDER BY ${orderBy}
		ORDER BY T.REQ_NO ASC
	
	
	) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wh.dao.WhReqRelDDao.countWhReqRelD */
SELECT COUNT(*) CNT
FROM WH_REQ_REL_D T

mapper.insert:
/* com.innobiz.orange.web.wh.dao.WhReqRelDDao.insertWhReqRelD */ INSERT INTO WH_REQ_REL_D
REQ_NO, REL_NO, SORT_ORDR,
#{reqNo, jdbcType=INTEGER}, #{relNo, jdbcType=INTEGER}, #{sortOrdr, jdbcType=INTEGER},

mapper.update:
/* com.innobiz.orange.web.wh.dao.WhReqRelDDao.updateWhReqRelD */
UPDATE WH_REQ_REL_D

	 SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},


	 AND REQ_NO = #{reqNo, jdbcType=INTEGER}
	 AND REL_NO = #{relNo, jdbcType=INTEGER}

mapper.delete:
/* com.innobiz.orange.web.wh.dao.WhReqRelDDao.deleteWhReqRelD */
DELETE FROM WH_REQ_REL_D

	 AND REQ_NO = #{reqNo, jdbcType=INTEGER}
	 AND REL_NO = #{relNo, jdbcType=INTEGER}
