mapper.select:
/* com.innobiz.orange.web.wl.dao.WlTaskLogRDao.selectWlTaskLogR */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.LOG_NO ASC) AS RNUM,
	
	T.LOG_NO,
	T.REGR_UID,

FROM WL_TASK_LOG_R T 


	ORDER BY ${orderBy}
	ORDER BY T.LOG_NO ASC


) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wl.dao.WlTaskLogRDao.countWlTaskLogR */
SELECT COUNT(*) CNT
FROM WL_TASK_LOG_R T

