LOG_NO, REGR_UID,
#{logNo, jdbcType=INTEGER}, #{regrUid, jdbcType=VARCHAR},

mapper.delete:
/* com.innobiz.orange.web.wl.dao.WlTaskLogRDao.deleteWlTaskLogR */
DELETE FROM WL_TASK_LOG_R

	 AND LOG_NO = #{logNo, jdbcType=INTEGER}
	 AND REGR_UID = #{regrUid, jdbcType=VARCHAR}
