mapper.select:
/* com.innobiz.orange.web.wo.dao.WoOnecAuthDDao.countWoOnecAuthD */
SELECT COUNT(*) CNT
FROM WO_ONEC_AUTH_D T

	AND T.ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wo.dao.WoOnecAuthDDao.insertWoOnecAuthD */ INSERT INTO WO_ONEC_AUTH_D
ONEC_NO, USER_UID, AUTH_TYP_CD, REGR_UID, REG_DT,
#{onecNo, jdbcType=BIGINT}, #{userUid, jdbcType=VARCHAR}, #{authTypCd, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.wo.dao.WoOnecAuthDDao.updateWoOnecAuthD */
UPDATE WO_ONEC_AUTH_D

	ONEC_NO = #{onecNo, jdbcType=BIGINT},
	USER_UID = #{userUid, jdbcType=VARCHAR},
	AUTH_TYP_CD = #{authTypCd, jdbcType=VARCHAR},
	REGR_UID = #{regrUid, jdbcType=VARCHAR},
	
		REG_DT = GETDATE(),
		REG_DT = NULL,
		REG_DT = CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),
	


	AND ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wo.dao.WoOnecAuthDDao.deleteWoOnecAuthD */
DELETE FROM WO_ONEC_AUTH_D

	AND ONEC_NO = #{onecNo, jdbcType=BIGINT}
	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	${whereSqllet}
