mapper.select:
/* com.innobiz.orange.web.wp.dao.WpMpBDao.selectWpMpB */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.MP_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.MP_ID,
	T.MP_NM,
	T.MP_GRADE,
	T.MP_PHONE,
	T.MP_EMAIL,
	CONVERT(VARCHAR(19), T.REG_DT, 120) REG_DT,
	T.REG_UID,

FROM WP_MP_B T

	AND T.MP_ID = #{mpId, jdbcType=VARCHAR}
	AND T.MP_NM LIKE '%' + #{mpNm, jdbcType=VARCHAR} + '%'
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.MP_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wp.dao.WpMpBDao.countWpMpB */
SELECT COUNT(*) CNT
FROM WP_MP_B T

	AND T.MP_ID = #{mpId, jdbcType=VARCHAR}
	AND T.MP_NM LIKE '%' + #{mpNm, jdbcType=VARCHAR} + '%'
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wp.dao.WpMpBDao.insertWpMpB */ INSERT INTO WP_MP_B
MP_ID, MP_NM, MP_GRADE, MP_PHONE, MP_EMAIL, REG_DT, REG_UID,
#{mpId, jdbcType=VARCHAR}, #{mpNm, jdbcType=VARCHAR}, #{mpGrade, jdbcType=VARCHAR}, #{mpPhone, jdbcType=VARCHAR}, #{mpEmail, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{regUid, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wp.dao.WpMpBDao.updateWpMpB */ UPDATE WP_MP_B
MP_ID = #{mpId, jdbcType=VARCHAR}, MP_NM = #{mpNm, jdbcType=VARCHAR}, MP_GRADE = #{mpGrade, jdbcType=VARCHAR}, MP_PHONE = #{mpPhone, jdbcType=VARCHAR}, MP_EMAIL = #{mpEmail, jdbcType=VARCHAR},
REG_DT = GETDATE(), REG_DT = NULL, REG_DT = CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),
REG_UID = #{regUid, jdbcType=VARCHAR},
AND MP_ID = #{mpId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wp.dao.WpMpBDao.deleteWpMpB */
DELETE FROM WP_MP_B

	AND MP_ID = #{mpId, jdbcType=VARCHAR}
	${whereSqllet}
