mapper.select:
/* com.innobiz.orange.web.wp.dao.WpPrjAuthDDao.selectWpPrjAuthD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.PRJ_NO DESC, N.AUTH_CD DESC, N.MP_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.PRJ_NO,
	T.AUTH_CD,
	T.MP_ID,

FROM WP_PRJ_AUTH_D T

	AND T.PRJ_NO = #{prjNo, jdbcType=BIGINT}
	AND T.AUTH_CD = #{authCd, jdbcType=VARCHAR}
	AND T.MP_ID = #{mpId, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.PRJ_NO, T.AUTH_CD, T.MP_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wp.dao.WpPrjAuthDDao.countWpPrjAuthD */
SELECT COUNT(*) CNT
FROM WP_PRJ_AUTH_D T

	AND T.PRJ_NO = #{prjNo, jdbcType=BIGINT}
	AND T.AUTH_CD = #{authCd, jdbcType=VARCHAR}
	AND T.MP_ID = #{mpId, jdbcType=VARCHAR}
	${whereSqllet}

