/* com.innobiz.orange.web.wp.dao.WpPrjAuthDDao.insertWpPrjAuthD */ INSERT INTO WP_PRJ_AUTH_D
PRJ_NO, AUTH_CD, MP_ID,
#{prjNo, jdbcType=BIGINT}, #{authCd, jdbcType=VARCHAR}, #{mpId, jdbcType=VARCHAR},

