	AND PRJ_NO = #{prjNo, jdbcType=BIGINT}
	AND AUTH_CD = #{authCd, jdbcType=VARCHAR}
	AND MP_ID = #{mpId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wp.dao.WpPrjAuthDDao.deleteWpPrjAuthD */
