mapper.select:
/* com.innobiz.orange.web.wp.dao.WpPrjPichBDao.selectWpPrjPichB */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.PRJ_NO DESC, N.SEQ DESC) AS RNUM, N.* FROM (SELECT
	
	T.PRJ_NO,
	T.SEQ,
	T.PICH_NM,
	T.PICH_GRADE,
	T.PICH_PHON,
	T.PICH_EMAIL,
	T.NOTE,

FROM WP_PRJ_PICH_B T

	AND T.PRJ_NO = #{prjNo, jdbcType=BIGINT}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.PRJ_NO, T.SEQ


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wp.dao.WpPrjPichBDao.countWpPrjPichB */
SELECT COUNT(*) CNT
FROM WP_PRJ_PICH_B T

	AND T.PRJ_NO = #{prjNo, jdbcType=BIGINT}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wp.dao.WpPrjPichBDao.insertWpPrjPichB */ INSERT INTO WP_PRJ_PICH_B
PRJ_NO, SEQ, PICH_NM, PICH_GRADE, PICH_PHON, PICH_EMAIL, NOTE,
#{prjNo, jdbcType=BIGINT}, #{seq, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SEQ),0)+1 FROM WP_PRJ_PICH_B WHERE PRJ_NO = #{prjNo, jdbcType=VARCHAR}), #{pichNm, jdbcType=VARCHAR}, #{pichGrade, jdbcType=VARCHAR}, #{pichPhon, jdbcType=VARCHAR}, #{pichEmail, jdbcType=VARCHAR}, #{note, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wp.dao.WpPrjPichBDao.updateWpPrjPichB */
UPDATE WP_PRJ_PICH_B

	PRJ_NO = #{prjNo, jdbcType=BIGINT},
	SEQ = #{seq, jdbcType=INTEGER},
	PICH_NM = #{pichNm, jdbcType=VARCHAR},
	PICH_GRADE = #{pichGrade, jdbcType=VARCHAR},
	PICH_PHON = #{pichPhon, jdbcType=VARCHAR},
	PICH_EMAIL = #{pichEmail, jdbcType=VARCHAR},
	NOTE = #{note, jdbcType=VARCHAR},


	AND PRJ_NO = #{prjNo, jdbcType=BIGINT}
	AND SEQ = #{seq, jdbcType=INTEGER}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wp.dao.WpPrjPichBDao.deleteWpPrjPichB */
DELETE FROM WP_PRJ_PICH_B

	AND PRJ_NO = #{prjNo, jdbcType=BIGINT}
	AND SEQ = #{seq, jdbcType=INTEGER}
	${whereSqllet}
