mapper.select:
/* com.innobiz.orange.web.wr.dao.WrRezvBDao.selectWrRezvB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REZV_STRT_YMD DESC, N.REZV_STRT_TIME ASC) AS RNUM, N.* FROM ( SELECT
WR_T.REZV_ID, WR_T.COMP_ID, WR_T.RESC_KND_ID, WR_T.RESC_MNG_ID, CONVERT(VARCHAR(16),WR_T.REZV_STRT_DT,120) AS REZV_STRT_DT, CONVERT(VARCHAR(16),WR_T.REZV_END_DT,120) AS REZV_END_DT, WR_T.SUBJ, WR_T.CONT, WR_T.DISC_STAT_CD, WR_T.DISCR_UID, WR_T.DISC_DT, WR_T.DISC_CONT, WR_T.REGR_UID, CONVERT(VARCHAR(19), WR_T.REG_DT, 120) REG_DT, (SELECT RESC_VA FROM WR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM WR_RESC_KND_B WHERE RESC_KND_ID = WR_T.RESC_KND_ID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko') ) AS KND_NM, (SELECT RESC_VA FROM WR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM WR_RESC_MNG_B WHERE RESC_MNG_ID = WR_T.RESC_MNG_ID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko') ) AS RESC_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = WR_T.DISCR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) DISCR_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = WR_T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) REGR_NM, CONVERT(VARCHAR(10),REZV_STRT_DT,23) AS REZV_STRT_YMD, CONVERT(VARCHAR(5),REZV_STRT_DT,108) AS REZV_STRT_TIME, CONVERT(VARCHAR(10),REZV_END_DT,23) AS REZV_END_YMD, CONVERT(VARCHAR(5),REZV_END_DT,108) AS REZV_END_TIME, WR_T.SCHDL_ID, WR_T.SCHDL_KND_CD, (SELECT BGCOL_CD FROM WR_RESC_KND_B WHERE RESC_KND_ID = WR_T.RESC_KND_ID ) AS BGCOL_CD, RESQ_EMAIL_YN, RES_EMAIL_YN,
FROM WR_REZV_B WR_T
ORDER BY ${orderBy} ORDER BY CONVERT(VARCHAR(10),WR_T.REZV_STRT_DT,23) DESC, CONVERT(VARCHAR(5),WR_T.REZV_STRT_DT,108) ASC
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wr.dao.WrRezvBDao.countWrRezvB */
SELECT COUNT(*) CNT
FROM WR_REZV_B WR_T

mapper.insert:
/* com.innobiz.orange.web.wr.dao.WrRezvBDao.insertWrRezvB */ INSERT INTO WR_REZV_B
REZV_ID, COMP_ID, RESC_KND_ID, RESC_MNG_ID, REZV_STRT_DT, REZV_END_DT, SUBJ, CONT, DISC_STAT_CD, DISCR_UID, SCHDL_ID, SCHDL_KND_CD, BGCOL_CD, RESQ_EMAIL_YN, RES_EMAIL_YN, REGR_UID, REG_DT,
#{rezvId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{rescKndId, jdbcType=VARCHAR}, #{rescMngId, jdbcType=VARCHAR}, CONVERT(DATETIME, #{rezvStrtDt, jdbcType=VARCHAR}, 120), CONVERT(DATETIME, #{rezvEndDt, jdbcType=VARCHAR}, 120), #{subj, jdbcType=VARCHAR}, #{cont, jdbcType=VARCHAR}, #{discStatCd, jdbcType=VARCHAR}, #{discrUid, jdbcType=VARCHAR}, #{schdlId, jdbcType=VARCHAR}, #{schdlKndCd, jdbcType=VARCHAR}, #{bgcolCd, jdbcType=VARCHAR}, #{resqEmailYn, jdbcType=VARCHAR}, #{resEmailYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL,

mapper.update:
/* com.innobiz.orange.web.wr.dao.WrRezvBDao.updateWrRezvB */ UPDATE WR_REZV_B
RESC_KND_ID = #{rescKndId, jdbcType=VARCHAR}, RESC_MNG_ID = #{rescMngId, jdbcType=VARCHAR}, REZV_STRT_DT = CONVERT(DATETIME, #{rezvStrtDt, jdbcType=VARCHAR}, 120), REZV_END_DT = CONVERT(DATETIME, #{rezvEndDt, jdbcType=VARCHAR}, 120), SUBJ = #{subj, jdbcType=VARCHAR}, CONT = #{cont, jdbcType=VARCHAR}, DISC_STAT_CD = #{discStatCd, jdbcType=VARCHAR}, DISCR_UID = #{discrUid, jdbcType=VARCHAR},
DISC_DT = GETDATE(), DISC_DT = NULL, DISC_DT = CONVERT(DATETIME, #{discDt, jdbcType=VARCHAR}, 120),
DISC_CONT = #{discCont, jdbcType=VARCHAR}, SCHDL_ID = #{schdlId, jdbcType=VARCHAR}, SCHDL_KND_CD = #{schdlKndCd, jdbcType=VARCHAR}, BGCOL_CD = #{bgcolCd, jdbcType=VARCHAR}, RESQ_EMAIL_YN = #{resqEmailYn, jdbcType=VARCHAR}, RES_EMAIL_YN = #{resEmailYn, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND REZV_ID = #{rezvId, jdbcType=VARCHAR} AND REGR_UID = #{regrUid, jdbcType=VARCHAR} AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wr.dao.WrRezvBDao.deleteWrRezvB */
DELETE FROM WR_REZV_B

	 AND REZV_ID = #{rezvId, jdbcType=VARCHAR}
	 AND REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
