mapper.select:
/* com.innobiz.orange.web.wv.dao.WvSurvFileDDao.selectWvSurvFileD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.FILE_ID,N.DISP_ORDR DESC) AS RNUM, N.* FROM ( SELECT
WSF_T.FILE_ID, WSF_T.REF_ID, WSF_T.DISP_NM, WSF_T.DISP_ORDR, WSF_T.FILE_EXT, WSF_T.FILE_SIZE, WSF_T.SAVE_PATH, WSF_T.USE_YN, WSF_T.REGR_UID, WSF_T.REG_DT,
FROM WV_SURV_FILE_D WSF_T
ORDER BY ${orderBy} ORDER BY WSF_T.FILE_ID,WSF_T.DISP_ORDR
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wv.dao.WvSurvFileDDao.countWvSurvFileD */
SELECT COUNT(*) CNT
FROM WV_SURV_FILE_D WSFD_T

mapper.insert:
/* com.innobiz.orange.web.wv.dao.WvSurvFileDDao.insertWvSurvFileD */ INSERT INTO WV_SURV_FILE_D
FILE_ID, REF_ID, DISP_NM, DISP_ORDR, FILE_EXT, FILE_SIZE, SAVE_PATH, USE_YN, REGR_UID, REG_DT,
#{fileId, jdbcType=INTEGER}, #{refId, jdbcType=VARCHAR}, #{dispNm, jdbcType=NVARCHAR}, #{dispOrdr, jdbcType=INTEGER}, #{fileExt, jdbcType=VARCHAR}, #{fileSize, jdbcType=INTEGER}, #{savePath, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.wv.dao.WvSurvFileDDao.updateWvSurvFileD */
UPDATE WV_SURV_FILE_D

	 DISP_ORDR = #{dispOrdr, jdbcType=INTEGER},
	 USE_YN = #{useYn, jdbcType=VARCHAR},


	 AND FILE_ID = #{fileId, jdbcType=NUMERIC}
	 AND REF_ID = #{refId, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wv.dao.WvSurvFileDDao.deleteWvSurvFileD */
DELETE FROM WV_SURV_FILE_D

	 AND FILE_ID = #{fileId, jdbcType=NUMERIC}
	 AND REF_ID = #{refId, jdbcType=VARCHAR}
	
	
	AND SAVE_PATH LIKE '%'+#{savePath, jdbcType=VARCHAR}+'%'
