mapper.select:
/* com.innobiz.orange.web.wv.dao.WvSurvPopupDDao.selectWvSurvPopupD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}COMP_ID DESC) AS RNUM,
SURV_ID, COMP_ID, WDTH_VA, USE_YN, MODR_UID, MOD_DT,
FROM WV_SURV_POPUP_D
ORDER BY ${orderBy} ORDER BY COMP_ID DESC
) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

mapper.insert:
/* com.innobiz.orange.web.wv.dao.wvSurvPopupDDao.insertwvSurvPopupD */ INSERT INTO WV_SURV_POPUP_D
SURV_ID, COMP_ID, WDTH_VA, USE_YN, MODR_UID, MOD_DT,
#{survId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{wdthVa, jdbcType=INTEGER}, #{useYn, jdbcType=VARCHAR}, #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.wv.dao.wvSurvPopupDDao.updatewvSurvPopupD */
UPDATE WV_SURV_POPUP_D

	 WDTH_VA = #{wdthVa, jdbcType=INTEGER},
	 USE_YN = #{useYn, jdbcType=VARCHAR},
	MODR_UID = #{modrUid, jdbcType=VARCHAR},
	
		MOD_DT = GETDATE(),
		MOD_DT = NULL,
		MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
	


	AND SURV_ID = #{survId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wv.dao.wvSurvPopupDDao.deletewvSurvPopupD */
DELETE FROM WV_SURV_POPUP_D

	AND SURV_ID = #{survId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
