mapper.select:
/* com.innobiz.orange.web.ap.dao.ApErpFormBDao.countApErpFormB */
SELECT COUNT(*) CNT
FROM AP_ERP_FORM_B T, AP_RESC_D R

	AND R.COMP_ID = T.COMP_ID AND R.LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') AND R.RESC_ID = T.RESC_ID AND R.RESC_VA LIKE CONCAT('%', #{schWord, jdbcType=VARCHAR}, '%')
	AND T.ERP_FORM_ID = #{erpFormId, jdbcType=VARCHAR}
	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.ERP_FORM_TYP_CD = #{erpFormTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApErpFormBDao.insertApErpFormB */ INSERT INTO AP_ERP_FORM_B
ERP_FORM_ID, COMP_ID, ERP_FORM_TYP_CD, RESC_ID, REG_URL, INFM_URL,
#{erpFormId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{erpFormTypCd, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{regUrl, jdbcType=NVARCHAR}, #{infmUrl, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApErpFormBDao.updateApErpFormB */
UPDATE AP_ERP_FORM_B

	ERP_FORM_ID = #{erpFormId, jdbcType=VARCHAR},
	COMP_ID = #{compId, jdbcType=VARCHAR},
	ERP_FORM_TYP_CD = #{erpFormTypCd, jdbcType=VARCHAR},
	RESC_ID = #{rescId, jdbcType=VARCHAR},
	REG_URL = #{regUrl, jdbcType=NVARCHAR},
	INFM_URL = #{infmUrl, jdbcType=NVARCHAR},


	AND ERP_FORM_ID = #{erpFormId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApErpFormBDao.deleteApErpFormB */
DELETE FROM AP_ERP_FORM_B

	AND ERP_FORM_ID = #{erpFormId, jdbcType=VARCHAR}
	${whereSqllet}
