mapper.select:
/* com.innobiz.orange.web.ap.dao.ApFormBDao.countApFormB */
SELECT COUNT(*) CNT
FROM AP_FORM_B T, AP_RESC_D R

	AND R.COMP_ID = T.COMP_ID AND R.LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') AND R.RESC_ID = T.RESC_ID AND R.RESC_VA LIKE CONCAT('%', #{schWord, jdbcType=VARCHAR}, '%')
	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.FORM_BX_ID = #{formBxId, jdbcType=VARCHAR}
	AND T.FORM_TYP_CD = #{formTypCd, jdbcType=VARCHAR}
	AND T.TPL_YN = #{tplYn, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	AND T.ERP_FORM_ID = #{erpFormId, jdbcType=VARCHAR}
	${whereSqllet}

