mapper.select:
/* com.innobiz.orange.web.ap.dao.ApFormCombDDao.selectApFormCombD */
SELECT

	T.FORM_ID,
	T.FORM_COMB_ID,
	T.FORM_COMB_SEQ,
	T.SORT_ORDR,

FROM AP_FORM_COMB_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_COMB_ID = #{formCombId, jdbcType=VARCHAR}
	AND T.FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.FORM_ID, T.FORM_COMB_ID, T.FORM_COMB_SEQ

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApFormCombDDao.countApFormCombD */
SELECT COUNT(*) CNT
FROM AP_FORM_COMB_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_COMB_ID = #{formCombId, jdbcType=VARCHAR}
	AND T.FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApFormCombDDao.insertApFormCombD */ INSERT INTO AP_FORM_COMB_D
FORM_ID, FORM_COMB_ID, FORM_COMB_SEQ, SORT_ORDR,
#{formId, jdbcType=VARCHAR}, #{formCombId, jdbcType=VARCHAR}, #{formCombSeq, jdbcType=INTEGER}, #{sortOrdr, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SORT_ORDR),0)+1 FROM AP_FORM_COMB_D P WHERE P.FORM_ID = #{formId, jdbcType=VARCHAR}),

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApFormCombDDao.updateApFormCombD */
UPDATE AP_FORM_COMB_D

	FORM_ID = #{formId, jdbcType=VARCHAR},
	FORM_COMB_ID = #{formCombId, jdbcType=VARCHAR},
	FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER},
	SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},


	AND FORM_ID = #{formId, jdbcType=VARCHAR}
	AND FORM_COMB_ID = #{formCombId, jdbcType=VARCHAR}
	AND FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApFormCombDDao.deleteApFormCombD */
DELETE FROM AP_FORM_COMB_D

	AND FORM_ID = #{formId, jdbcType=VARCHAR}
	AND FORM_COMB_ID = #{formCombId, jdbcType=VARCHAR}
	AND FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	${whereSqllet}
