#{formId, jdbcType=VARCHAR}, #{formImgTypCd, jdbcType=VARCHAR}, #{imgPath, jdbcType=VARCHAR}, NULL#{imgWdth, jdbcType=INTEGER}, NULL#{imgHght, jdbcType=INTEGER},

