/* com.innobiz.orange.web.ap.dao.ApFormImgDDao.updateApFormImgD */
UPDATE AP_FORM_IMG_D

	FORM_ID = #{formId, jdbcType=VARCHAR},
	FORM_IMG_TYP_CD = #{formImgTypCd, jdbcType=VARCHAR},
	IMG_PATH = #{imgPath, jdbcType=VARCHAR},
	IMG_WDTH = NULL#{imgWdth, jdbcType=INTEGER},
	IMG_HGHT = NULL#{imgHght, jdbcType=INTEGER},


	AND FORM_ID = #{formId, jdbcType=VARCHAR}
	AND FORM_IMG_TYP_CD = #{formImgTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApFormImgDDao.deleteApFormImgD */
