mapper.select:
/* com.innobiz.orange.web.ap.dao.ApFormXmlDDao.selectApFormXmlD */
SELECT

	T.FORM_ID,
	T.FORM_XML_CD,
	T.FORM_XML_CONT,

FROM AP_FORM_XML_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_XML_CD = #{formXmlCd, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.FORM_ID, T.FORM_XML_CD

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApFormXmlDDao.countApFormXmlD */
SELECT COUNT(*) CNT
FROM AP_FORM_XML_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_XML_CD = #{formXmlCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApFormXmlDDao.insertApFormXmlD */ INSERT INTO AP_FORM_XML_D
FORM_ID, FORM_XML_CD, FORM_XML_CONT,
#{formId, jdbcType=VARCHAR}, #{formXmlCd, jdbcType=VARCHAR}, #{formXmlCont, jdbcType=CLOB},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApFormXmlDDao.updateApFormXmlD */
UPDATE AP_FORM_XML_D

	FORM_ID = #{formId, jdbcType=VARCHAR},
	FORM_XML_CD = #{formXmlCd, jdbcType=VARCHAR},
	FORM_XML_CONT = #{formXmlCont, jdbcType=CLOB},


	AND FORM_ID = #{formId, jdbcType=VARCHAR}
	AND FORM_XML_CD = #{formXmlCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApFormXmlDDao.deleteApFormXmlD */
DELETE FROM AP_FORM_XML_D

	AND FORM_ID = #{formId, jdbcType=VARCHAR}
	AND FORM_XML_CD = #{formXmlCd, jdbcType=VARCHAR}
	${whereSqllet}
