mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdAttFileLDao.selectApOngdAttFileL */
SELECT

	T.APV_NO,
	T.ATT_HST_NO,
	T.ATT_SEQ,
	T.ATT_DISP_NM,
	T.FILE_EXT,
	T.FILE_KB,
	T.FILE_PATH,
	T.MODR_UID,
	DATE_FORMAT(T.MOD_DT, '%Y-%m-%d %H:%i:%s') MOD_DT,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.MODR_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) MODR_NM,

FROM AP_${storage}_ATT_FILE_L T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.ATT_HST_NO = #{attHstNo, jdbcType=INTEGER}
	AND T.ATT_SEQ = #{attSeq, jdbcType=INTEGER}
	AND T.ATT_SEQ IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.APV_NO, T.ATT_HST_NO, T.ATT_SEQ

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdAttFileLDao.countApOngdAttFileL */
SELECT COUNT(*) CNT
FROM AP_${storage}_ATT_FILE_L T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.ATT_HST_NO = #{attHstNo, jdbcType=INTEGER}
	AND T.ATT_SEQ = #{attSeq, jdbcType=INTEGER}
	AND T.ATT_SEQ IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdAttFileLDao.insertApOngdAttFileL */ INSERT INTO AP_${storage}_ATT_FILE_L
APV_NO, ATT_HST_NO, ATT_SEQ, ATT_DISP_NM, FILE_EXT, FILE_KB, FILE_PATH, MODR_UID, MOD_DT,
#{apvNo, jdbcType=BIGINT}, #{attHstNo, jdbcType=INTEGER}, #{attSeq, jdbcType=INTEGER}, #{attDispNm, jdbcType=NVARCHAR}, #{fileExt, jdbcType=VARCHAR}, NULL#{fileKb, jdbcType=INTEGER}, #{filePath, jdbcType=VARCHAR}, #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdAttFileLDao.updateApOngdAttFileL */ UPDATE AP_${storage}_ATT_FILE_L
APV_NO = #{apvNo, jdbcType=BIGINT}, ATT_HST_NO = #{attHstNo, jdbcType=INTEGER}, ATT_SEQ = #{attSeq, jdbcType=INTEGER}, ATT_DISP_NM = #{attDispNm, jdbcType=NVARCHAR}, FILE_EXT = #{fileExt, jdbcType=VARCHAR}, FILE_KB = NULL#{fileKb, jdbcType=INTEGER}, FILE_PATH = #{filePath, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = NOW(), MOD_DT = NULL, MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND APV_NO = #{apvNo, jdbcType=BIGINT} AND ATT_HST_NO = #{attHstNo, jdbcType=INTEGER} AND ATT_SEQ = #{attSeq, jdbcType=INTEGER} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdAttFileLDao.deleteApOngdAttFileL */
DELETE FROM AP_${storage}_ATT_FILE_L

	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND ATT_HST_NO = #{attHstNo, jdbcType=INTEGER}
	AND ATT_SEQ = #{attSeq, jdbcType=INTEGER}
	${whereSqllet}
