mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdCnclDDao.selectApOngdCnclD */
SELECT

	T.MAKR_UID,
	T.XML_TYP_ID,
	T.APV_NO,

FROM AP_ONGD_CNCL_D T

	AND T.MAKR_UID = #{makrUid, jdbcType=VARCHAR}
	AND T.XML_TYP_ID = #{xmlTypId, jdbcType=VARCHAR}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.MAKR_UID, T.XML_TYP_ID, T.APV_NO

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdCnclDDao.countApOngdCnclD */
SELECT COUNT(*) CNT
FROM AP_ONGD_CNCL_D T

	AND T.MAKR_UID = #{makrUid, jdbcType=VARCHAR}
	AND T.XML_TYP_ID = #{xmlTypId, jdbcType=VARCHAR}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdCnclDDao.insertApOngdCnclD */ INSERT INTO AP_ONGD_CNCL_D
MAKR_UID, XML_TYP_ID, APV_NO,
#{makrUid, jdbcType=VARCHAR}, #{xmlTypId, jdbcType=VARCHAR}, #{apvNo, jdbcType=BIGINT},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdCnclDDao.updateApOngdCnclD */
UPDATE AP_ONGD_CNCL_D

	MAKR_UID = #{makrUid, jdbcType=VARCHAR},
	XML_TYP_ID = #{xmlTypId, jdbcType=VARCHAR},
	APV_NO = #{apvNo, jdbcType=BIGINT},


	AND MAKR_UID = #{makrUid, jdbcType=VARCHAR}
	AND XML_TYP_ID = #{xmlTypId, jdbcType=VARCHAR}
	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdCnclDDao.deleteApOngdCnclD */
DELETE FROM AP_ONGD_CNCL_D

	AND MAKR_UID = #{makrUid, jdbcType=VARCHAR}
	AND XML_TYP_ID = #{xmlTypId, jdbcType=VARCHAR}
	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}
