FROM AP_${storage}_PUB_BX_CNFM_L T

	AND T.PUB_BX_DEPT_ID = #{pubBxDeptId, jdbcType=VARCHAR}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.PUB_BX_DEPT_ID, T.APV_NO, T.USER_UID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

