mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdRefDocLDao.selectApOngdRefDocL */
SELECT

	T.APV_NO,
	T.REF_DOC_HST_NO,
	T.REF_APV_NO,
	T.SORT_ORDR,
	T.MODR_UID,
	DATE_FORMAT(T.MOD_DT, '%Y-%m-%d %H:%i:%s') MOD_DT,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.MODR_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) MODR_NM,

FROM AP_${storage}_REF_DOC_L T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.REF_DOC_HST_NO = #{refDocHstNo, jdbcType=INTEGER}
	AND T.REF_APV_NO = #{refApvNo, jdbcType=BIGINT}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.APV_NO, T.REF_DOC_HST_NO, T.REF_APV_NO

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdRefDocLDao.countApOngdRefDocL */
SELECT COUNT(*) CNT
FROM AP_${storage}_REF_DOC_L T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.REF_DOC_HST_NO = #{refDocHstNo, jdbcType=INTEGER}
	AND T.REF_APV_NO = #{refApvNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdRefDocLDao.insertApOngdRefDocL */ INSERT INTO AP_${storage}_REF_DOC_L
APV_NO, REF_DOC_HST_NO, REF_APV_NO, SORT_ORDR, MODR_UID, MOD_DT,
#{apvNo, jdbcType=BIGINT}, #{refDocHstNo, jdbcType=INTEGER}, #{refApvNo, jdbcType=BIGINT}, #{sortOrdr, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SORT_ORDR),0)+1 FROM AP_${storage}_REF_DOC_L P WHERE P.APV_NO = #{apvNo, jdbcType=VARCHAR} AND P.REF_DOC_HST_NO = #{refDocHstNo, jdbcType=VARCHAR}), #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdRefDocLDao.updateApOngdRefDocL */
UPDATE AP_${storage}_REF_DOC_L

	APV_NO = #{apvNo, jdbcType=BIGINT},
	REF_DOC_HST_NO = #{refDocHstNo, jdbcType=INTEGER},
	REF_APV_NO = #{refApvNo, jdbcType=BIGINT},
	SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},
	MODR_UID = #{modrUid, jdbcType=VARCHAR},
	
		MOD_DT = NOW(),
		MOD_DT = NULL,
		MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND REF_DOC_HST_NO = #{refDocHstNo, jdbcType=INTEGER}
	AND REF_APV_NO = #{refApvNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdRefDocLDao.deleteApOngdRefDocL */
DELETE FROM AP_${storage}_REF_DOC_L

	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND REF_DOC_HST_NO = #{refDocHstNo, jdbcType=INTEGER}
	AND REF_APV_NO = #{refApvNo, jdbcType=BIGINT}
	${whereSqllet}
