mapper.select:
/* com.innobiz.orange.web.ap.dao.ApStorRDao.selectApStorR */
SELECT

	T.APV_NO,
	T.STOR_ID,

FROM AP_STOR_R T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.STOR_ID = #{storId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.APV_NO, T.STOR_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApStorRDao.countApStorR */
SELECT COUNT(*) CNT
FROM AP_STOR_R T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.STOR_ID = #{storId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApStorRDao.insertApStorR */ INSERT INTO AP_STOR_R
APV_NO, STOR_ID,
#{apvNo, jdbcType=BIGINT}, #{storId, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApStorRDao.updateApStorR */
UPDATE AP_STOR_R

	APV_NO = #{apvNo, jdbcType=BIGINT},
	STOR_ID = #{storId, jdbcType=VARCHAR},


	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND STOR_ID = #{storId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApStorRDao.deleteApStorR */
DELETE FROM AP_STOR_R

	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND STOR_ID = #{storId, jdbcType=VARCHAR}
	${whereSqllet}
