mapper.select:
/* com.innobiz.orange.web.ap.dao.ApTranBDao.countApTranB */
SELECT COUNT(*) CNT
FROM AP_TRAN_B T, AP_RESC_B R

	AND R.LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') AND R.RESC_ID = T.STOR_RESC_ID AND R.RESC_VA LIKE CONCAT('%', #{schWord, jdbcType=VARCHAR}, '%')
	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApTranBDao.insertApTranB */ INSERT INTO AP_TRAN_B
TRAN_ID, COMP_ID, STOR_ID, STOR_RESC_ID, TRAN_TGT_STRT_YMD, TRAN_TGT_END_YMD, DOC_INCL_YN, PAP_DOC_INCL_YN, DOC_TGT_CNT, TRX_TGT_CNT, PAP_TGT_CNT, TRAN_STRT_DT, TRAN_END_DT, TRAN_PROC_STAT_CD, DEFRAG_YN, USER_UID,
#{tranId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{storId, jdbcType=VARCHAR}, #{storRescId, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{tranTgtStrtYmd, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), NOW(),NULL, STR_TO_DATE(#{tranTgtEndYmd, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{docInclYn, jdbcType=VARCHAR}, #{papDocInclYn, jdbcType=VARCHAR}, NULL#{docTgtCnt, jdbcType=BIGINT}, NULL#{trxTgtCnt, jdbcType=BIGINT}, NULL#{papTgtCnt, jdbcType=BIGINT}, NOW(),NULL, STR_TO_DATE(#{tranStrtDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), NOW(),NULL, STR_TO_DATE(#{tranEndDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{tranProcStatCd, jdbcType=VARCHAR}, #{defragYn, jdbcType=VARCHAR}, #{userUid, jdbcType=VARCHAR},

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApTranBDao.deleteApTranB */
DELETE FROM AP_TRAN_B

	AND TRAN_ID = #{tranId, jdbcType=VARCHAR}
	${whereSqllet}
