mapper.select:
/* com.innobiz.orange.web.bb.dao.BaBullPopupDDao.selectBaBullPopupD */
SELECT

	BULL_ID,
	BRD_ID,
	START_DT,
	END_DT,
	WIDTH,
	DISP_YN,
	USE_YN,
	COMP_ID,
	MODR_UID,
	DATE_FORMAT(MOD_DT, '%Y-%m-%d %H:%i:%s') MOD_DT,

FROM BA_BULL_POPUP_D

	 AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND BULL_ID = #{bullId, jdbcType=VARCHAR}
	 
		
			 AND DATE_FORMAT(START_DT, '%Y-%m-%d') >= #{todayYmd, jdbcType=VARCHAR}
			 AND DATE_FORMAT(END_DT, '%Y-%m-%d') <= #{todayYmd, jdbcType=VARCHAR}
		 
	 
	 AND USE_YN = #{useYn, jdbcType=VARCHAR}

ORDER BY ${orderBy}
ORDER BY BRD_ID DESC

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaBullPopupDDao.countBaBullPopupD */
SELECT COUNT(*) CNT
FROM BA_BULL_POPUP_D 

	 AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND BULL_ID = #{bullId, jdbcType=VARCHAR}
	 
		
			 AND DATE_FORMAT(START_DT, '%Y-%m-%d') >= #{todayYmd, jdbcType=VARCHAR}
			 AND DATE_FORMAT(END_DT, '%Y-%m-%d') <= #{todayYmd, jdbcType=VARCHAR}
		 
	 
	 AND USE_YN = #{useYn, jdbcType=VARCHAR}

mapper.insert:
/* com.innobiz.orange.web.bb.dao.baBullPopupDDao.insertbaBullPopupD */ INSERT INTO BA_BULL_POPUP_D
BULL_ID, BRD_ID, START_DT, END_DT, WIDTH, DISP_YN, USE_YN, COMP_ID, MODR_UID, MOD_DT,
#{bullId, jdbcType=VARCHAR}, #{brdId, jdbcType=VARCHAR}, STR_TO_DATE(#{strtDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), STR_TO_DATE(#{endDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{width, jdbcType=INTEGER}, #{dispYn, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.bb.dao.baBullPopupDDao.updatebaBullPopupD */ UPDATE BA_BULL_POPUP_D
START_DT = STR_TO_DATE(#{strtDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), END_DT = STR_TO_DATE(#{endDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), WIDTH = #{width, jdbcType=INTEGER}, DISP_YN = #{dispYn, jdbcType=VARCHAR}, DISP_YN = #{dispYn, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, COMP_ID = #{compId, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = NOW(), MOD_DT = NULL, MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND BULL_ID = #{bullId, jdbcType=INTEGER} AND BRD_ID = #{brdId, jdbcType=VARCHAR} AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.bb.dao.baBullPopupDDao.deletebaBullPopupD */
DELETE FROM BA_BULL_POPUP_D

	AND BULL_ID = #{bullId, jdbcType=INTEGER}
	AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	AND COMP_ID = #{compId, jdbcType=VARCHAR}
