mapper.select:
/* com.innobiz.orange.web.bb.dao.BaBullPopupDispDDao.selectBaBullPopupDispD */
SELECT

	BULL_ID,
	BRD_ID,
	USER_UID,
	UPDT_DT,

FROM BA_BULL_POPUP_DISP_D

	 AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND BULL_ID = #{bullId, jdbcType=VARCHAR}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 
		
			 AND DATE_FORMAT(UPDT_DT, '%Y-%m-%d') >= #{strtDt, jdbcType=VARCHAR}
			 AND DATE_FORMAT(UPDT_DT, '%Y-%m-%d') <= #{endDt, jdbcType=VARCHAR}
		 
	 

ORDER BY ${orderBy}
ORDER BY BRD_ID DESC

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaBullPopupDispDDao.countBaBullPopupDispD */
SELECT COUNT(*) CNT
FROM BA_BULL_POPUP_DISP_D 

	 AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND BULL_ID = #{bullId, jdbcType=VARCHAR}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 
		
			 AND DATE_FORMAT(UPDT_DT, '%Y-%m-%d') >= #{strtDt, jdbcType=VARCHAR}
			 AND DATE_FORMAT(UPDT_DT, '%Y-%m-%d') <= #{endDt, jdbcType=VARCHAR}

mapper.insert:
/* com.innobiz.orange.web.bb.dao.baBullPopupDispDDao.insertbaBullPopupDispD */ INSERT INTO BA_BULL_POPUP_DISP_D
BULL_ID, BRD_ID, USER_UID, UPDT_DT,
#{bullId, jdbcType=VARCHAR}, #{brdId, jdbcType=VARCHAR}, #{userUid, jdbcType=VARCHAR}, NOW(),

