/* com.innobiz.orange.web.bb.dao.baBullPopupDispDDao.updatebaBullPopupDispD */
UPDATE BA_BULL_POPUP_DISP_D

	UPDT_DT = NOW(),


	AND BULL_ID = #{bullId, jdbcType=INTEGER}
	AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	AND USER_UID = #{userUid, jdbcType=VARCHAR}

