mapper.select:
/* com.innobiz.orange.web.bb.dao.BaTblColmDDao.selectBaTblColmD */ SELECT
T.COLM_ID, T.TBL_ID, T.COLM_NM, T.COLM_DISP_NM, T.RESC_ID, (SELECT RESC_VA FROM BA_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM, T.DATA_TYP, T.COLM_LEN, T.EX_COLM_YN, T.COLM_TYP, T.COLM_TYP_VAL, T.REG_DISP_YN, T.MOD_DISP_YN, T.READ_DISP_YN, T.LIST_DISP_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM BA_TBL_COLM_D T, BA_RESC_B R
AND T.COLM_ID = #{colmId, jdbcType=VARCHAR} AND T.TBL_ID = #{tblId, jdbcType=VARCHAR} AND T.EX_COLM_YN = #{exColmYn, jdbcType=VARCHAR} ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.COLM_ID ASC
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaTblColmDDao.countBaTblColmD */
SELECT COUNT(*) CNT
FROM BA_TBL_COLM_D T, BA_RESC_B R

	 AND T.COLM_ID = #{colmId, jdbcType=VARCHAR}
	 AND T.TBL_ID = #{tblId, jdbcType=VARCHAR}
	 AND T.EX_COLM_YN = #{exColmYn, jdbcType=VARCHAR}
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.bb.dao.BaTblColmDDao.insertBaTblColmD */ INSERT INTO BA_TBL_COLM_D
COLM_ID, TBL_ID, COLM_NM, COLM_DISP_NM, RESC_ID, DATA_TYP, COLM_LEN, EX_COLM_YN, COLM_TYP, COLM_TYP_VAL, REG_DISP_YN, MOD_DISP_YN, READ_DISP_YN, LIST_DISP_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{colmId, jdbcType=VARCHAR}, #{tblId, jdbcType=VARCHAR}, #{colmNm, jdbcType=VARCHAR}, #{colmDispNm, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{dataTyp, jdbcType=VARCHAR}, #{colmLen, jdbcType=INTEGER}, #{exColmYn, jdbcType=VARCHAR}, #{colmTyp, jdbcType=VARCHAR}, #{colmTypVal, jdbcType=VARCHAR}, #{regDispYn, jdbcType=VARCHAR}, #{modDispYn, jdbcType=VARCHAR}, #{readDispYn, jdbcType=VARCHAR}, #{listDispYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, NOW(),NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.pt.dao.BaTblColmDDao.updateBaTblColmD */ UPDATE BA_TBL_COLM_D
COLM_NM = #{colmNm, jdbcType=VARCHAR}, COLM_DISP_NM = #{colmDispNm, jdbcType=VARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, DATA_TYP = #{dataTyp, jdbcType=VARCHAR}, COLM_LEN = #{colmLen, jdbcType=INTEGER}, COLM_TYP = #{colmTyp, jdbcType=VARCHAR}, COLM_TYP_VAL = #{colmTypVal, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = NOW(), MOD_DT = NULL, MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND COLM_ID = #{colmId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.bb.dao.BaTblColmDDao.deleteBaTblColmD */
DELETE FROM BA_TBL_COLM_D

	 AND COLM_ID = #{colmId, jdbcType=VARCHAR}
	 AND TBL_ID = #{tblId, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}
