mapper.select:
/* com.innobiz.orange.web.ct.dao.CtBlonCatDDao.selectCtBlonCatD */

SELECT


	CBCD_T.COMP_ID,
	CBCD_T.CT_ID,
	CBCD_T.CAT_ID,
	

FROM CT_BLON_CAT_D CBCD_T

ORDER BY ${orderBy}
ORDER BY CBCD_T.CAT_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtBlonCatDDao.countCtBlonCatD */
SELECT COUNT(*) CNT
FROM CT_BLON_CAT_D CBCD_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtBlonCatDDao.insertCtBlonCatD */ INSERT INTO CT_BLON_CAT_D
COMP_ID, CT_ID, CAT_ID,
#{compId, jdbcType=CHAR}, #{ctId, jdbcType=VARCHAR}, #{catId, jdbcType=VARCHAR},

mapper.update:
		/* com.innobiz.orange.web.ct.dao.CtBlonCatDDao.updateCtBlonCatD */
		UPDATE CT_BLON_CAT_D
		
		     CAT_ID = #{catId, jdbcType=VARCHAR},
		
		
			 AND COMP_ID = #{compId, jdbcType=CHAR}
   		     AND CT_ID = #{ctId, jdbcType=VARCHAR}

mapper.delete:
		/* com.innobiz.orange.web.ct.dao.CtBlonCatDDao.deleteCtBlonCatD */
		DELETE FROM CT_BLON_CAT_D
		
		     AND COMP_ID = #{compId, jdbcType=CHAR}
   		     AND CT_ID = #{ctId, jdbcType=VARCHAR}
   		     AND CAT_ID = #{catId, jdbcType=VARCHAR}
