mapper.select:
/* com.innobiz.orange.web.ct.dao.CtPltFncListDDao.selectCtPltFncListD */
SELECT

	T.USER_UID,
	T.BX_ID,
	T.SORT_ORDR,

FROM CT_PLT_FNC_LIST_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.PLT_ID = #{pltId, jdbcType=VARCHAR}
	AND T.BX_ID = #{bxId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.USER_UID, T.BX_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtPltFncListDDao.countCtPltFncListD */
SELECT COUNT(*) CNT
FROM CT_PLT_FNC_LIST_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.PLT_ID = #{pltId, jdbcType=VARCHAR}
	AND T.BX_ID = #{bxId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtPltFncListDDao.insertCtPltFncListD */ INSERT INTO CT_PLT_FNC_LIST_D
USER_UID, PLT_ID, BX_ID, SORT_ORDR,
#{userUid, jdbcType=VARCHAR}, #{pltId, jdbcType=VARCHAR}, #{bxId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SORT_ORDR),0)+1 FROM CT_PLT_FNC_LIST_D P WHERE P.USER_UID = #{userUid, jdbcType=VARCHAR}),

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtPltFncListDDao.updateCtPltFncListD */
