mapper.select:
/* com.innobiz.orange.web.ct.dao.CtRecMastCmdDDao.selectCtRecMastCmdD */

SELECT


	CRMCD_T.CMT_ID, CRMCD_T.BULL_ID, CRMCD_T.CMT,
	CRMCD_T.REGR_UID, CRMCD_T.REG_DT, 
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CRMCD_T.REGR_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM,
	

FROM CT_REC_MAST_CMD_D CRMCD_T


ORDER BY ${orderBy}
ORDER BY CRMCD_T.CMT_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtRecMastCmdDDao.countCtRecMastCmdD */
SELECT COUNT(*) CNT
FROM CT_REC_MAST_CMD_D CRMCD_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtRecMastCmdDDao.insertCtRecMastCmdD */ INSERT INTO CT_REC_MAST_CMD_D
CMT_ID, BULL_ID, CMT, REGR_UID, REG_DT,
#{cmtId, jdbcType=NUMERIC}, #{bullId, jdbcType=NUMERIC}, #{cmt, jdbcType=NVARCHAR}, #{regrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
	/* com.innobiz.orange.web.ct.dao.CtRecMastCmdDDao.updateCtRecMastCmdD */
	UPDATE CT_REC_MAST_CMD_D
	
		 CMT = #{cmt, jdbcType=VARCHAR},
		
	
	
	     AND CMT_ID = #{cmtId, jdbcType=NUMERIC}
	     AND BULL_ID = #{bullId, jdbcType=NUMERIC}

mapper.delete:
	/* com.innobiz.orange.web.ct.dao.CtRecMastCmdDDao.deleteCtRecMastCmdD */
	DELETE FROM CT_REC_MAST_CMD_D
	
	     AND CMT_ID = #{cmtId, jdbcType=NUMERIC}
	     AND BULL_ID = #{bullId, jdbcType=NUMERIC}
