mapper.select:
/* com.innobiz.orange.web.ct.dao.CtRecmdHstLDao.selectCtRecmdHstL */
SELECT

	CRH_T.BULL_ID,
	CRH_T.USER_UID,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CRH_T.USER_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) USER_NM,
	CRH_T.REG_DT,

FROM CT_RECMD_HST_L CRH_T

	 AND CRH_T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND CRH_T.USER_UID = #{userUid, jdbcType=VARCHAR}
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY CRH_T.REG_DT DESC

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtRecmdHstLDao.countCtRecmdHstL */
SELECT COUNT(*) CNT
FROM CT_RECMD_HST_L CRH_T

	 AND CRH_T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND CRH_T.USER_UID = #{userUid, jdbcType=VARCHAR}
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtRecmdHstLDao.insertCtRecmdHstL */ INSERT INTO CT_RECMD_HST_L
BULL_ID, USER_UID, REG_DT,
#{bullId, jdbcType=INTEGER}, #{userUid, jdbcType=VARCHAR}, NOW(),

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtRecmdHstLDao.updateCtRecmdHstL */
UPDATE CT_RECMD_HST_L

	REG_DT = NOW(),


	 AND BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ct.dao.CtRecmdHstLDao.deleteCtRecmdHstL */
DELETE FROM CT_RECMD_HST_L

	 AND BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}
