mapper.select:
/* com.innobiz.orange.web.ct.dao.CtSurvReplyDDao.selectCtSurvReplyD */

SELECT


	CSRD_T.QUES_ID, CSRD_T.COMP_ID, CSRD_T.SURV_ID, CSRD_T.REPLY_NO,
	CSRD_T.REPLYR_UID, CSRD_T.REPLYR_DEPT_ID, CSRD_T.REPLY_DT, 
	CSRD_T.MULC_INPUT_REPLY_CONT, CSRD_T.OEND_REPLY_CONT, 
	
	

FROM CT_SURV_REPLY_D CSRD_T


ORDER BY ${orderBy}
ORDER BY CSRD_T.REPLY_NO

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtSurvReplyDDao.countCtSurvReplyD */
SELECT COUNT(*) CNT
FROM CT_SURV_REPLY_D CSRD_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtSurvReplyDDao.insertCtSurvReplyD */ INSERT INTO CT_SURV_REPLY_D
QUES_ID, COMP_ID, SURV_ID, REPLY_NO, REPLYR_UID, REPLYR_DEPT_ID, REPLY_DT, MULC_INPUT_REPLY_CONT, OEND_REPLY_CONT,
#{quesId, jdbcType=VARCHAR},#{compId, jdbcType=CHAR},#{survId, jdbcType=VARCHAR}, #{replyNo, jdbcType=NUMERIC},#{replyrUid, jdbcType=VARCHAR},#{replyrDeptId, jdbcType=VARCHAR}, STR_TO_DATE(#{replyDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),#{mulcInputReplyCont, jdbcType=NVARCHAR},#{oendReplyCont, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtSurvReplyDDao.updateCtSurvReplyD */
UPDATE CT_SURV_REPLY_D

	 REPLYR_UID = #{replyrUid, jdbcType=VARCHAR},
	 REPLYR_DEPT_ID = #{replyrDeptId, jdbcType=VARCHAR},
	 REPLY_DT = STR_TO_DATE(#{replyDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	 MULC_INPUT_REPLY_CONT = #{mulcInputReplyCont, jdbcType=NVARCHAR},
	 OEND_REPLY_CONT = #{oendReplyCont, jdbcType=NVARCHAR},


     AND QUES_ID = #{quesId, jdbcType=VARCHAR}
     AND COMP_ID = #{compId, jdbcType=CHAR}
	 AND SURV_ID = #{survId, jdbcType=VARCHAR}
	 AND REPLY_NO = #{replyNo, jdbcType=NUMERIC}

mapper.delete:
/* com.innobiz.orange.web.ct.dao.CtSurvReplyDDao.deleteCtSurvReplyD */
DELETE FROM CT_SURV_REPLY_D

     AND QUES_ID = #{quesId, jdbcType=VARCHAR}
     AND COMP_ID = #{compId, jdbcType=CHAR}
	 AND SURV_ID = #{survId, jdbcType=VARCHAR}
	 AND REPLYR_UID = #{replyrUid, jdbcType=VARCHAR}
	 AND REPLY_NO = #{replyNo, jdbcType=NUMERIC}
