mapper.select:
/* com.innobiz.orange.web.cu.dao.CuNoteRecvBDao.selectCuNoteRecvL */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.RECV_DT DESC) AS RNUM,
T.RECV_NO, T.SEND_NO, T.RECVR_UID, T.RECVR_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.RECVR_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) RECVR_RESC_NM, T.RECV_DT
FROM CU_NOTE_RECV_L T
ORDER BY ${orderBy} ORDER BY T.RECV_DT DESC
) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.cu.dao.CuNoteRecvBDao.countCuNoteRecvL */
SELECT COUNT(*) CNT
FROM CU_NOTE_RECV_L T

mapper.insert:
/* com.innobiz.orange.web.cu.dao.CuNoteRecvBDao.insertCuNoteRecvL */ INSERT INTO CU_NOTE_RECV_L
RECV_NO, SEND_NO, RECVR_UID, RECVR_NM, RECV_DT,
#{recvNo, jdbcType=INTEGER}, #{sendNo, jdbcType=INTEGER}, #{recvrUid, jdbcType=VARCHAR}, #{recvrNm, jdbcType=NVARCHAR}, NOW(),NULL, STR_TO_DATE(#{recvDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.cu.dao.CuNoteRecvBDao.updateCuNoteRecvL */
UPDATE CU_NOTE_RECV_L

	 RECVR_NM = #{recvrNm, jdbcType=NVARCHAR},
	
		 RECV_DT = NOW(),
		 RECV_DT = NULL,
		 RECV_DT = STR_TO_DATE(#{recvDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	 AND RECV_NO = #{recvNo, jdbcType=VARCHAR}
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.cu.dao.CuNoteRecvBDao.deleteCuNoteRecvL */
DELETE FROM CU_NOTE_RECV_L

	 AND RECV_NO = #{recvNo, jdbcType=VARCHAR}
	 AND SEND_NO = #{sendNo, jdbcType=VARCHAR}
