mapper.select:
/* com.innobiz.orange.web.dm.dao.DmClsRDao.selectDmClsR */
SELECT

	T.DOC_GRP_ID,
	T.CLS_ID,

FROM DM_${tableName}_CLS_R T

ORDER BY ${orderBy}
ORDER BY T.CLS_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmClsRDao.countDmClsR */
SELECT COUNT(*) CNT
FROM DM_${tableName}_CLS_R T

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmClsRDao.insertDmClsR */ INSERT INTO DM_${tableName}_CLS_R
DOC_GRP_ID, CLS_ID,
#{docGrpId, jdbcType=VARCHAR}, #{clsId, jdbcType=VARCHAR},

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmClsRDao.deleteDmClsR */
DELETE FROM DM_${tableName}_CLS_R

	 AND DOC_GRP_ID = #{docGrpId, jdbcType=VARCHAR}
	 AND CLS_ID = #{clsId, jdbcType=VARCHAR}
