mapper.select:
/* com.innobiz.orange.web.dm.dao.DmItemBDao.selectDmItemB */ SELECT
T.STOR_ID, T.ITEM_ID, T.ITEM_NM, T.RESC_ID, T.DATA_TYP, T.COLM_LEN, T.ITEM_TYP, T.ITEM_TYP_VA, T.REG_DISP_YN, T.MOD_DISP_YN, T.READ_DISP_YN, T.LIST_DISP_YN, T.USE_YN, T.ADD_ITEM_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) REGR_NM, (SELECT RESC_VA FROM DM_RESC_B WHERE STOR_ID = T.STOR_ID AND RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS ITEM_DISP_NM ,
FROM DM_ITEM_B T , DM_RESC_B R
ORDER BY ${orderBy} ORDER BY T.REG_DT DESC
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

