#{storId, jdbcType=VARCHAR}, #{itemId, jdbcType=VARCHAR}, #{itemNm, jdbcType=NVARCHAR}, #{itemDispNm, jdbcType=VARCHAR}, #{rescId, jdbcType=NVARCHAR}, #{dataTyp, jdbcType=VARCHAR}, #{colmLen, jdbcType=INTEGER}, #{itemTyp, jdbcType=VARCHAR}, #{itemTypVa, jdbcType=VARCHAR}, #{regDispYn, jdbcType=VARCHAR}, #{modDispYn, jdbcType=VARCHAR}, #{readDispYn, jdbcType=VARCHAR}, #{listDispYn, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{addItemYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR},NULL, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

